import React, { useState } from 'react';
import axios from 'axios';

const PaymentForm = () => {
  const [paymentData, setPaymentData] = useState({
    sale_id: '',
    total_amount: '',
    installment_number: '',
    installment_amount: '',
    installment_total: '',
    payment_date: ''
  });
  
  const handleChange = (e) => {
    setPaymentData({
        ...paymentData,
        [e.target.name]: e.target.value
    });
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
        await axios.post('http://localhost:5000/payments', paymentData);
        alert('Payment data submitted!');
        setPaymentData({
            sale_id: '',
            total_amount: '',
            installment_number: '',
            installment_amount: '',
            installment_total: '',
            payment_date: ''
        })
    } catch (error) {
        console.error('There was an error submitting the payment data!', error);
    }
  }

  return (
    <form onSubmit={handleSubmit} className="max-w-lg mx-auto p-6 bg-white rounded-lg shadow-md mt-10">
      <h2 className="text-2xl font-bold mb-6 text-center">Payment Form</h2>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Sale ID:</label>
        <input
          type="text"
          name="sale_id"
          value={paymentData.sale_id}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Total Amount:</label>
        <input
          type="number"
          name="total_amount"
          value={paymentData.total_amount}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Nomor Cicilan:</label>
        <input
          type="text"
          name="installment_number"
          value={paymentData.installment_number}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Jumlah Cicilan (Rp):</label>
        <input
          type="text"
          name="installment_amount"
          value={paymentData.installment_amount}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Total Cicilan:</label>
        <input
          type="text"
          name="installment_total"
          value={paymentData.installment_total}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />
      </div>
      <div className="mb-6">
        <label className="block text-gray-700 text-sm font-bold mb-2">Payment Date:</label>
        <input
          type="date"
          name="payment_date"
          value={paymentData.payment_date}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />
      </div>
      <button
        type="submit"
        className="w-full bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 focus:outline-none focus:bg-blue-600"
      >
        Submit Payment
      </button>
    </form>
  );
}

export default PaymentForm;
