import React, { useState } from 'react';
import axios from 'axios';

const PaymentForm = () => {
  const [paymentData, setPaymentData] = useState({
    sale_id: '',
    total_amount: '',
    installment_number: '',
    installment_amount: '',
    installment_total: '',
    payment_date: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Update the payment data
    setPaymentData((prevState) => ({
      ...prevState,
      [name]: value
    }));

    // Automatically calculate installment amount when total_amount or installment_total is updated
    if (name === 'total_amount' || name === 'installment_total') {
      const totalAmount = name === 'total_amount' ? value : prevState.total_amount;
      const installmentTotal = name === 'installment_total' ? value : prevState.installment_total;
      if (totalAmount && installmentTotal) {
        const calculatedInstallmentAmount = totalAmount / installmentTotal;
        setPaymentData((prevState) => ({
          ...prevState,
          installment_amount: calculatedInstallmentAmount.toFixed(2) // rounding to two decimal places
        }));
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/payments', paymentData);
      alert('Data pembayaran berhasil dikirim!');
      setPaymentData({
        sale_id: '',
        total_amount: '',
        installment_number: '',
        installment_amount: '',
        installment_total: '',
        payment_date: ''
      });
    } catch (error) {
      console.error('Terjadi kesalahan saat mengirim data pembayaran!', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-lg mx-auto p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold text-center mb-4">Form Pembayaran</h2>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">ID Penjualan:</label>
        <input
          type="text"
          name="sale_id"
          value={paymentData.sale_id}
          onChange={handleChange}
          required
          className="w-full px-3 py-2 border border-gray-300 rounded-md"
        />
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Jumlah Total:</label>
        <input
          type="number"
          name="total_amount"
          value={paymentData.total_amount}
          onChange={handleChange}
          required
          className="w-full px-3 py-2 border border-gray-300 rounded-md"
        />
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Total Cicilan:</label>
        <select
          name="installment_total"
          value={paymentData.installment_total}
          onChange={handleChange}
          required
          className="w-full px-3 py-2 border border-gray-300 rounded-md"
        >
          <option value="">Pilih Total Cicilan</option>
          <option value="1">1x</option>
          <option value="3">3x</option>
          <option value="6">6x</option>
          <option value="12">12x</option>
        </select>
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Jumlah Cicilan:</label>
        <input
          type="text"
          name="installment_amount"
          value={paymentData.installment_amount}
          onChange={handleChange}
          required
          readOnly
          className="w-full px-3 py-2 border border-gray-300 rounded-md bg-gray-100"
        />
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">Tanggal Pembayaran:</label>
        <input
          type="date"
          name="payment_date"
          value={paymentData.payment_date}
          onChange={handleChange}
          required
          className="w-full px-3 py-2 border border-gray-300 rounded-md"
        />
      </div>
      <button
        type="submit"
        className="w-full bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 transition duration-200"
      >
        Kirim Pembayaran
      </button>
    </form>
  );
};

export default PaymentForm;
