import React, { useState } from 'react'
import axios from 'axios';

const PaymentForm = () => {
  const [paymentData, setPaymentData] = useState({
    sale_id: '',
    total_amount: '',
    installment_number: '',
    installment_amount: '',
    installment_total: '',
    payment_date: ''
  });
  
  const handleChange = (e) => {
    setPaymentData({
        ...paymentData,
        [e.target.name]: e.target.value
    });
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
        await axios.post('http://localhost:5000/payments', paymentData);
        alert('Payment data submitted!');
        setPaymentData({
            sale_id: '',
            total_amount: '',
            installment_number: '',
            installment_amount: '',
            installment_total: '',
            payment_date: ''
        })
    } catch (error) {
        console.error('There was an error submitting the payment data!', error);
    }
  }

  return (
    <form onSubmit={ handleSubmit }>
        <label>
            Sale ID:
            <input type='text' name='sale_id' value={ paymentData.sale_id } onChange={ handleChange } required />
        </label>
        <label>
            Total Amount:
            <input type='number' name='total_amount' value={ paymentData.total_amount } onChange={ handleChange } required />
        </label>
        <label>
            Installment Number:
            <input type='text' name='installment_number' value={ paymentData.installment_number } onChange={ handleChange } required />
        </label>
        <label>
            Installment Amount:
            <input type='text' name='installment_amount' value={ paymentData.installment_amount } onChange={ handleChange } required />
        </label>
        <label>
            Installment Total:
            <input type='text' name='total' value={ paymentData.installment_total } onChange={ handleChange } required />
        </label>
        <label>
            Payment Date:
            <input type='date' name='payment_date' value={ paymentData.payment_date } onChange={ handleChange } required />
        </label>
        <button type='submit'>
            Submit Payment
        </button>
    </form>
  )
}

export default PaymentForm
